// Map payload
const body = $json.body || $json;

// Normalizar tel√©fono a formato E.164 (+57...)
function normalizePhone(phone) {
  if (!phone) return '';
  let clean = phone.replace(/[^0-9+]/g, '');
  if (!clean.startsWith('+')) clean = '+' + clean;
  return clean;
}

const payload = {
  project_id: body.project_id,
  client_name: body.client_name || body.project_id || 'unknown',
  status: body.status || 'active',
  phone_filter: normalizePhone(body.phone_filter),
  prompt_template: body.prompt_template || '',
  conversion_config: body.conversion_config || null,
  openai_model: body.openai_model || 'gpt-4o-mini',
  openai_temperature: Number(body.openai_temperature) || 0.3,
  openai_max_tokens: Number(body.openai_max_tokens) || 150,
  click_matching_window_days: Number(body.click_matching_window_days) || 60,
  message_limit_per_conversation: Number(body.message_limit_per_conversation) || 15,
  sheet_spreadsheet_id: body.sheet_spreadsheet_id || '',
  sheet_messages_name: body.sheet_messages_name || 'chats_raw',
  sheet_conversions_name: body.sheet_conversions_name || 'conversions'
};

if (!payload.project_id) {
  throw new Error('project_id is required');
}

return [{ json: payload }];
