// ============================================
// AGRUPAR MENSAJES POR PROJECT_ID + PHONE
// Mode: Run Once for All Items
// ============================================

const items = $input.all();
const groups = {};

console.log('?? Total mensajes recibidos:', items.length);

for (const item of items) {
  const key = `${item.json.project_id}:${item.json.phone_e164}`;

  if (!groups[key]) {
    // Parsear conversion_config si viene como string
    let conversionConfig = item.json.conversion_config;
    if (typeof conversionConfig === 'string') {
      try {
        conversionConfig = JSON.parse(conversionConfig);
      } catch (e) {
        conversionConfig = {
          "1": { "name": "no_qualified", "value": 0, "currency": "COP" },
          "2": { "name": "lead_qualified", "value": 15000, "currency": "COP" },
          "3": { "name": "sale_confirmed", "value": 85000, "currency": "COP" }
        };
      }
    }

    groups[key] = {
      project_id: item.json.project_id,
      phone_e164: item.json.phone_e164,
      config: {
        client_name: item.json.client_name,
        prompt_template: item.json.prompt_template,
        conversion_config: conversionConfig,
        openai_model: item.json.openai_model || 'gpt-4o-mini',
        openai_temperature: parseFloat(item.json.openai_temperature) || 0.3,
        openai_max_tokens: parseInt(item.json.openai_max_tokens) || 150,
        click_matching_window_days: parseInt(item.json.click_matching_window_days) || 60,
        message_limit_per_conversation: parseInt(item.json.message_limit_per_conversation) || 15,
        sheet_spreadsheet_id: item.json.sheet_spreadsheet_id,
        sheet_conversions_name: item.json.sheet_conversions_name || 'conversions'
      },
      messages: [],
      event_ids: []
    };
  }

  groups[key].messages.push({
    ts: item.json.ts,
    text: item.json.message_text || '',
    direction: item.json.direction,
    click_id_hash: item.json.click_id_hash || null,
    lead_email: item.json.lead_email || null,
    lead_name: item.json.lead_name || null
  });

  groups[key].event_ids.push(item.json.event_id);
}

const result = Object.values(groups);
console.log('?? Grupos creados:', result.length);

return result.map(group => ({ json: group }));

// ============================================
// PROCESAR CONVERSACI?N
// Mode: Run Once for Each Item
// ============================================

const group = $input.item.json;

console.log('\n=== Procesando ===');
console.log('Project:', group.project_id);
console.log('Phone:', group.phone_e164);

if (!group.messages || !Array.isArray(group.messages)) {
  throw new Error('No messages array. Keys: ' + Object.keys(group).join(', '));
}

if (!group.config || !group.config.prompt_template) {
  throw new Error('No config.prompt_template. Config keys: ' + Object.keys(group.config || {}).join(', '));
}

const messages = group.messages;

if (messages.length === 0) {
  console.log('?? Sin mensajes');
  return {
    ...group,
    aggregated_conversation: '[No hay mensajes]',
    message_count: 0,
    message_count_total: 0,
    first_message_ts: new Date().toISOString(),
    last_message_ts: new Date().toISOString(),
    click_id_hash: null,
    lead_email: null,
    lead_name: null,
    click_data: null,
    has_click: false
  };
}

const sortedMessages = [...messages].sort((a, b) => new Date(a.ts) - new Date(b.ts));

const first_message_ts = sortedMessages[0].ts;
const last_message_ts = sortedMessages[sortedMessages.length - 1].ts;

// Ventana reciente para reducir tokens
const DEFAULT_MAX_MESSAGES = 15;
const HARD_CAP_MESSAGES = 30;
const configuredMax = parseInt(group.config.message_limit_per_conversation);
const MAX_MESSAGES = Math.min(
  Math.max(Number.isFinite(configuredMax) ? configuredMax : DEFAULT_MAX_MESSAGES, 1),
  HARD_CAP_MESSAGES
);
const recentMessages = sortedMessages.slice(-MAX_MESSAGES);

const conversation = recentMessages
  .map(m => {
    const direction = m.direction === 'in' ? 'CLIENTE' : 'AGENTE';
    const text = m.text && m.text.trim() ? m.text : '[Multimedia/Sin texto]';
    return `${direction}: ${text}`;
  })
  .join('\n');

console.log('Mensajes total:', sortedMessages.length, ' | usados:', recentMessages.length);
console.log('? Conversaci?n:', conversation.length, 'chars');

// Rate limiting: 500ms entre items
await new Promise(resolve => setTimeout(resolve, 500));

// Extraer click_id_hash/email/nombre del primer inbound (o del primero disponible)
const firstInboundMsg = sortedMessages.find(m => m.direction === 'in') || sortedMessages[0];
const click_id_hash = firstInboundMsg?.click_id_hash || null;

const lead_email = (
  sortedMessages.find(m => m.direction === 'in' && m.lead_email)?.lead_email ||
  sortedMessages.find(m => m.lead_email)?.lead_email ||
  null
);

const lead_name = (
  sortedMessages.find(m => m.direction === 'in' && m.lead_name)?.lead_name ||
  sortedMessages.find(m => m.lead_name)?.lead_name ||
  null
);

console.log('Click ID Hash:', click_id_hash);
if (lead_email) console.log('Lead Email:', lead_email);
if (lead_name) console.log('Lead Name:', lead_name);

return {
  project_id: group.project_id,
  phone_e164: group.phone_e164,
  config: group.config,
  event_ids: group.event_ids,
  aggregated_conversation: conversation,
  message_count: recentMessages.length,
  message_count_total: sortedMessages.length,
  first_message_ts,
  last_message_ts,
  click_id_hash,
  lead_email,
  lead_name,
  click_data: null,
  has_click: false
};

// ============================================
// MERGE: Combinar conversación con click
// ============================================

const lookup = $input.item.json;
const allConvos = $('Process Conversation').all();

// Get matched convo using pairing or index fallback
let convo;
try { convo = $('Process Conversation').json; } catch(e) {}
if (!convo || convo.phone_e164 !== lookup.phone_e164) {
  convo = allConvos.find(c => c.json.project_id === lookup.project_id && c.json.phone_e164 === lookup.phone_e164)?.json || allConvos[$input.itemIndex]?.json;
}

if (!convo) {
  throw new Error(`No conversation found matched in Merge Click Data for ${lookup.project_id}:${lookup.phone_e164}`);
}

// Verificar si hay click válido
let click_data = null;
let has_click = false;

if (lookup.click_event_id) {
  click_data = lookup;
  has_click = true;
} 

return {
  ...convo,
  click_data,
  has_click
};

