// ============================================
// PARSE AI RESPONSE + USER IDENTIFIERS (SHA-256)
// ============================================

function sha256(ascii) {
  function rightRotate(value, amount) {
    return (value >>> amount) | (value << (32 - amount));
  }
  const mathPow = Math.pow;
  const maxWord = mathPow(2, 32);
  let result = '';
  const words = [];
  const asciiBitLength = ascii.length * 8;
  let hash = sha256.h || [];
  let k = sha256.k || [];
  let primeCounter = k.length;
  const isComposite = {};
  for (let candidate = 2; primeCounter < 64; candidate++) {
    if (!isComposite[candidate]) {
      for (let i = 0; i < 313; i += candidate) {
        isComposite[i] = candidate;
      }
      hash[primeCounter] = (mathPow(candidate, 0.5) * maxWord) | 0;
      k[primeCounter++] = (mathPow(candidate, 1 / 3) * maxWord) | 0;
    }
  }
  sha256.h = hash;
  sha256.k = k;
  ascii += '\x80';
  while (ascii.length % 64 - 56) ascii += '\x00';
  for (let i = 0; i < ascii.length; i++) {
    const j = ascii.charCodeAt(i);
    words[i >> 2] |= j << ((3 - i) % 4) * 8;
  }
  words[words.length] = (asciiBitLength / maxWord) | 0;
  words[words.length] = asciiBitLength;
  for (let j = 0; j < words.length; ) {
    const w = words.slice(j, (j += 16));
    const oldHash = hash;
    hash = hash.slice(0, 8);
    for (let i = 0; i < 64; i++) {
      const w15 = w[i - 15], w2 = w[i - 2];
      const a = hash[0], e = hash[4];
      const temp1 = hash[7] + (rightRotate(e, 6) ^ rightRotate(e, 11) ^ rightRotate(e, 25)) + ((e & hash[5]) ^ (~e & hash[6])) + k[i] + (w[i] = i < 16 ? w[i] : (w[i - 16] + (rightRotate(w15, 7) ^ rightRotate(w15, 18) ^ (w15 >>> 3)) + w[i - 7] + (rightRotate(w2, 17) ^ rightRotate(w2, 19) ^ (w2 >>> 10))) | 0);
      const temp2 = (rightRotate(a, 2) ^ rightRotate(a, 13) ^ rightRotate(a, 22)) + ((a & hash[1]) ^ (a & hash[2]) ^ (hash[1] & hash[2]));
      hash = [(temp1 + temp2) | 0].concat(hash);
      hash[4] = (hash[4] + temp1) | 0;
      hash.pop();
    }
    for (let i = 0; i < 8; i++) hash[i] = (hash[i] + oldHash[i]) | 0;
  }
  for (let i = 0; i < 8; i++) {
    for (let j = 3; j + 1; j--) {
      const b = (hash[i] >> (j * 8)) & 255;
      result += (b < 16 ? '0' : '') + b.toString(16);
    }
  }
  return result;
}

function normalizeEmail(email) {
  if (!email) return null;
  return String(email).trim().toLowerCase() || null;
}

function normalizePhoneE164(phone) {
  if (!phone) return null;
  return String(phone).trim() || null;
}

const openai_response = $input.item.json;

// ESTRATEGIA MÚLTIPLE para obtener datos de conversación:
// 1. Intentar pairing directo desde Classify with OpenAI (preserva pairing con Cache Hit?)
// 2. Intentar pairing desde Cache Hit? (último nodo antes del split)
// 3. Intentar pairing desde Cache Gate
// 4. Búsqueda por phone en todos los items de Cache Gate
let convo_data = null;

// Estrategia 1: Pairing desde Classify with OpenAI (hereda de Cache Hit? FALSE)
try {
  const paired = $('Classify with OpenAI').json;
  if (paired && paired.phone_e164) convo_data = paired;
} catch(e) {}

// Estrategia 2: Pairing desde Cache Gate
if (!convo_data) {
  try {
    const paired = $('Cache Gate').json;
    if (paired && paired.phone_e164) convo_data = paired;
  } catch(e) {}
}

// Estrategia 3: Búsqueda en todos los items de Cache Gate
if (!convo_data) {
  const allConvos = $('Cache Gate').all();
  // Intentar encontrar por itemIndex primero (puede fallar si IF filtró)
  if (allConvos[$input.itemIndex]?.json?.phone_e164) {
    convo_data = allConvos[$input.itemIndex].json;
  } else {
    // Buscar cualquier conversación válida (último recurso)
    for (const item of allConvos) {
      if (item.json && item.json.phone_e164 && item.json.has_cache === false) {
        convo_data = item.json;
        break;
      }
    }
  }
}

if (!convo_data || !convo_data.phone_e164) {
  // Debug info
  const debugInfo = {
    itemIndex: $input.itemIndex,
    openaiKeys: Object.keys(openai_response || {}),
    allConvosCount: $('Cache Gate').all().length
  };
  throw new Error('No conversation data found. Debug: ' + JSON.stringify(debugInfo));
}

let ai_result;
try {
  const content = openai_response.message?.content || openai_response.choices?.[0]?.message?.content || '{}';
  const cleanContent = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  ai_result = JSON.parse(cleanContent);
  if (typeof ai_result.label === 'undefined') ai_result.label = 1;
  if (typeof ai_result.confidence === 'undefined') ai_result.confidence = 0.5;
  if (!ai_result.reason) ai_result.reason = 'Clasificación automática';
  if (![1, 2, 3].includes(ai_result.label)) ai_result.label = 1;
} catch (error) {
  ai_result = { label: 1, confidence: 0.3, reason: 'Error parsing: ' + error.message };
}

const convConfig = convo_data.config.conversion_config;
const conversion_config = convConfig[ai_result.label.toString()] || { name: 'unknown', value: 0, currency: 'COP' };
const conversion_id = `conv_${Date.now()}_${Math.random().toString(36).substring(2, 10)}`;
const external_attrib_id = `conv-${convo_data.project_id}-${convo_data.phone_e164}-${ai_result.label}`;

let attribution_method = 'organic';
if (convo_data.click_data && convo_data.click_data.click_id) {
  attribution_method = convo_data.click_data.click_id_hash ? 'click_id_hash_match' : 'click_id_match';
}

const lead_email = normalizeEmail(convo_data.lead_email);
const email_sha256 = lead_email ? sha256(lead_email) : null;
const phone_sha256 = normalizePhoneE164(convo_data.phone_e164) ? sha256(normalizePhoneE164(convo_data.phone_e164)) : null;

return {
  conversion_id,
  project_id: convo_data.project_id,
  phone_e164: convo_data.phone_e164,
  click_event_id: convo_data.click_data?.click_event_id || null,
  click_id: convo_data.click_data?.click_id || null,
  click_id_type: convo_data.click_data?.click_id_type || null,
  attribution_method,
  ai_label: ai_result.label,
  ai_confidence: ai_result.confidence,
  ai_reason: ai_result.reason,
  ai_model_used: convo_data.config.openai_model || 'gpt-4o-mini',
  conversion_name: conversion_config.name,
  conversion_value: conversion_config.value,
  conversion_currency: conversion_config.currency || 'COP',
  conversion_time: new Date().toISOString(),
  external_attrib_id,
  aggregated_conversation: convo_data.aggregated_conversation,
  message_count: convo_data.message_count,
  first_message_ts: convo_data.first_message_ts,
  last_message_ts: convo_data.last_message_ts,
  event_ids: convo_data.event_ids,
  config: convo_data.config,
  lead_email,
  lead_name: (convo_data.lead_name && String(convo_data.lead_name).trim()) || null,
  email_sha256,
  phone_sha256
};

// Preparar event_ids para marcar como procesados
const data = $input.item.json;

if (!data || !data.event_ids) {
  return { success: false, message: 'No event_ids', event_ids_array: [] };
}

return {
  event_ids_array: data.event_ids,
  count: data.event_ids.length
};

// Preparar fila para Google Sheets
const data = $input.item.json;

if (!data) {
  throw new Error('No input data');
}

const formatDate = (isoString) => {
  const date = new Date(isoString);
  return date.toISOString().replace('T', ' ').substring(0, 19) + '-05:00';
};

const labelMap = {
  1: 'No Calificado',
  2: 'Lead Calificado',
  3: 'Venta Confirmada'
};

return {
  click_id: data.click_id || '',
  conversion_name: data.conversion_name,
  conversion_time: formatDate(data.conversion_time),
  conversion_value: data.conversion_value,
  conversion_currency: data.conversion_currency,
  phone_e164: data.phone_e164,
  ai_reason: data.ai_reason,
  ai_confidence: Math.round(data.ai_confidence * 100) / 100,
  external_attrib_id: data.external_attrib_id,
