  lead_name: data.lead_name || '',
  email_sha256: data.email_sha256 || '',
  phone_sha256: data.phone_sha256 || '',
  sheet_spreadsheet_id: data.config.sheet_spreadsheet_id,
  sheet_name: data.config.sheet_conversions_name || 'conversions'
