}

if (!convo) {
  throw new Error(`No conversation found matched in Cache Gate for ${lookup.project_id}:${lookup.phone_e164}`);
}

const hasCache = lookup.cache_ai_label !== undefined && lookup.cache_ai_label !== null;

const cache = hasCache ? {
  ai_label: lookup.cache_ai_label,
  ai_confidence: lookup.cache_ai_confidence,
  ai_reason: lookup.cache_ai_reason,
  conversion_name: lookup.cache_conversion_name,
  conversion_value: lookup.cache_conversion_value,
  conversion_currency: lookup.cache_conversion_currency,
  ai_model_used: lookup.cache_ai_model_used
