// Agrupar mensajes por project_id + phone_e164
const items = $input.all();
const groups = {};

for (const item of items) {
  const key = `${item.json.project_id}:${item.json.phone_e164}`;

  if (!groups[key]) {
    groups[key] = {
      project_id: item.json.project_id,
      phone_e164: item.json.phone_e164,
      config: {
        client_name: item.json.client_name,
        prompt_template: item.json.prompt_template,
        conversion_config: typeof item.json.conversion_config === 'string' 
          ? JSON.parse(item.json.conversion_config) 
          : item.json.conversion_config,
        openai_model: item.json.openai_model || 'gpt-4o-mini',
        openai_temperature: parseFloat(item.json.openai_temperature) || 0.3,
        openai_max_tokens: parseInt(item.json.openai_max_tokens) || 150,
        click_matching_window_days: parseInt(item.json.click_matching_window_days) || 60,
        sheet_spreadsheet_id: item.json.sheet_spreadsheet_id,
        sheet_conversions_name: item.json.sheet_conversions_name || 'conversions'
      },
      messages: [],
      event_ids: []
    };
  }

  groups[key].messages.push({
    ts: item.json.ts,
    text: item.json.message_text || '',
    direction: item.json.direction
  });

  groups[key].event_ids.push(item.json.event_id);
}

// Convertir a array de items
return Object.values(groups).map(group => ({
  json: group
}));

const group = $input.first().json;

// Filtrar mensajes con texto válido y ordenar por timestamp
const sortedMessages = group.messages
  .filter(m => m.text && m.text.trim() !== '')  // Filtrar mensajes vacíos
  .sort((a, b) => new Date(a.ts) - new Date(b.ts));

// Edge case: sin mensajes válidos
if (sortedMessages.length === 0) {
  return [
    {
      json: {
        project_id: group.project_id,
        phone_e164: group.phone_e164,
        config: group.config,
        event_ids: group.event_ids || [],
        aggregated_conversation: '[Sin mensajes de texto válidos]',
        message_count: 0,
        first_message_ts: new Date().toISOString(),
        last_message_ts: new Date().toISOString()
      }
    }
  ];
}

// Formatear conversación para OpenAI
const conversation = sortedMessages
  .map(m => {
    const prefix = m.direction === 'in' ? 'CLIENTE: ' : 'AGENTE: ';
    return prefix + m.text.trim();
  })
  .join('\n');

// Timestamps
const first_message_ts = sortedMessages[0].ts;
const last_message_ts = sortedMessages[sortedMessages.length - 1].ts;

return [
  {
    json: {
      project_id: group.project_id,
      phone_e164: group.phone_e164,
      config: group.config,
      event_ids: group.event_ids || [],
      aggregated_conversation: conversation,
      message_count: sortedMessages.length,
      first_message_ts,
      last_message_ts
    }
  }
];

// Combinar datos de conversación con click encontrado (si existe)
const convo = $('Aggregate Conversation').first().json;
const clickResult = $input.all();

let click_data = null;
if (clickResult.length > 0 && clickResult[0].json.click_event_id) {
  click_data = clickResult[0].json;
}

return {
  ...convo,
  click_data,
  has_click: click_data !== null
};

