const openai_response = $input.first().json;
const convo_data = $('Merge Click Data').first().json;

// Parsear respuesta JSON de OpenAI
let ai_result;
try {
  // OpenAI response viene en message.content
  const content = openai_response.message?.content || openai_response.choices?.[0]?.message?.content || '{}';

  // Limpiar posibles backticks de markdown
  const cleanContent = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  ai_result = JSON.parse(cleanContent);

  // Validar campos requeridos
  if (typeof ai_result.label === 'undefined') {
    ai_result.label = 1;
  }
  if (typeof ai_result.confidence === 'undefined') {
    ai_result.confidence = 0.5;
  }
  if (!ai_result.reason) {
    ai_result.reason = 'Clasificación automática';
  }

  // Validar label (1-3)
  if (![1, 2, 3].includes(ai_result.label)) {
    ai_result.label = 1;
  }

} catch (error) {
  // Fallback si falla el parseo
  ai_result = {
    label: 1,
    confidence: 0.3,
    reason: 'Error parsing AI response: ' + error.message
  };
}

// Obtener config de conversión según label
const conversion_config = convo_data.config.conversion_config[ai_result.label.toString()] || 
  { name: 'unknown', value: 0, currency: 'USD' };

// Generar conversion_id
const conversion_id = `conv_${Date.now()}_${Math.random().toString(36).substring(2, 10)}`;

// Determinar método de atribución
let attribution_method = 'organic';
if (convo_data.click_data) {
  attribution_method = convo_data.click_data.click_id_hash ? 'click_id_hash_match' : 'phone_match';
}

// External attrib ID para dedupe
const external_attrib_id = `conv-${convo_data.project_id}-${convo_data.phone_e164}-${ai_result.label}`;

// Timestamp de conversión
const conversion_time = new Date().toISOString();

return {
  conversion_id,
  project_id: convo_data.project_id,
  phone_e164: convo_data.phone_e164,
  click_event_id: convo_data.click_data?.click_event_id || null,
  click_id: convo_data.click_data?.click_id || null,
  click_id_type: convo_data.click_data?.click_id_type || null,
  attribution_method,
  ai_label: ai_result.label,
  ai_confidence: ai_result.confidence,
  ai_reason: ai_result.reason,
  ai_model_used: convo_data.config.openai_model || 'gpt-4o-mini',
  conversion_name: conversion_config.name,
  conversion_value: conversion_config.value,
  conversion_currency: conversion_config.currency || 'USD',
  conversion_time,
  external_attrib_id,
  aggregated_conversation: convo_data.aggregated_conversation,
  message_count: convo_data.message_count,
  first_message_ts: convo_data.first_message_ts,
  last_message_ts: convo_data.last_message_ts,
  event_ids: convo_data.event_ids,
  config: convo_data.config
};

// Preparar datos para marcar mensajes como procesados
const data = $('Parse AI Response').first().json;
const event_ids = data.event_ids || [];

if (event_ids.length === 0) {
  return { success: false, message: 'No event_ids to update' };
}

// Formatear para el query
return {
  event_ids_array: event_ids,
  count: event_ids.length
};

// Preparar fila para Google Sheets
const data = $('Parse AI Response').first().json;

// Formatear timestamp para Google Ads (Colombia UTC-5)
const formatDate = (isoString) => {
  const date = new Date(isoString);
  return date.toISOString().replace('T', ' ').substring(0, 19) + '-05:00';
};

// Formatear label como texto
const labelMap = {
  1: 'No Calificado',
  2: 'Lead Calificado',
  3: 'Venta Confirmada'
};

return {
  click_id: data.click_id || '',
  conversion_name: data.conversion_name,
  conversion_time: formatDate(data.conversion_time),
  conversion_value: data.conversion_value,
  conversion_currency: data.conversion_currency,
  phone_e164: data.phone_e164,
  ai_reason: data.ai_reason,
  ai_confidence: Math.round(data.ai_confidence * 100) / 100,
  external_attrib_id: data.external_attrib_id,
  label_text: labelMap[data.ai_label] || 'Unknown',
  sheet_spreadsheet_id: data.config.sheet_spreadsheet_id,
  sheet_name: data.config.sheet_conversions_name || 'conversions'
};
